#include <algorithm>
#include <string>
#include <iostream>

int basementFloor(std::string s) {
	int count = 0;
	int found = 0;


	for (int i = 0; i < s.size(); i++)
	{
		if (s[i] == '(')
		{
			count++;
		}
		else
		{
			count--;
		}
		
		if (count == -1) {
			return i+1;
		}

	}
}

int count(std::string s, char find) {
	int count = 0;

	for (int i = 0; i < s.size(); i++)
	{
		if (s[i] == find)
		{
			count++;
		}
	}

	return count;
}

